function [HFC] = sequential_search_algorithm(Unique_Matrix, Minimized_Matrix, W)  p = size(Minimized_Matrix)(2);  m = size(Unique_Matrix)(2);  HFC = [];  for i=1:p    S1=1;    S2=1;    S3=1;    iter = 1;    Est = W * [Unique_Matrix(S1), Unique_Matrix(S2), Unique_Matrix(S3)]';        while (Minimized_Matrix(i) - Est != 0)      S3 += 1;      if (S3 > m)        S2 += 1;        S3 = 1;      endif      if (S2 > m)        S1 += 1;        S2 = 1;      endif      if (S1 > m)        S1 = 1;      endif      Est = W * [Unique_Matrix(S1), Unique_Matrix(S2), Unique_Matrix(S3)]';      iter += 1;    endwhile    HFC = [HFC [Unique_Matrix(S1), Unique_Matrix(S2), Unique_Matrix(S3)]];  endfor  twelve_multiple = size(HFC)(2) / 12;  HFC = reshape(HFC, twelve_multiple, twelve_multiple)';endfunction